#!/bin/python3import mathimport osimport randomimport reimport sysimport pandas as pd## Complete the 'maximumProfit' function below.## The function is expected to return a LONG_INTEGER.# The function accepts INTEGER_ARRAY price as parameter.##%%def maximumProfit(price):    p = 0    cl = price    while cl != []:        l = dict(enumerate(cl))        f = max(l.values())        m = [i for i in l.keys() if l[i] == f]        p += m[-1]*f        for n in range(m[-1]):            p -= cl[n]        cl = cl[m[-1]+1:]    return p     # Write your code here#%%if __name__ == '__main__':    t = 3    for t_itr in range(t):        n = 3        price = [100,2,100]        profit = maximumProfit(price)    print(profit)#%%